function f= root(x)
f(1)=m*diff(x(1),t,2) - (2*l^2*m*(2*x(1)(t) - (2*R*cos(x(2)(t) - t*w))/l^2)*(2*diff(x(1),t)*diff(x(1)(t), t) + 2*x(1)(t)*diff(x(1),t,2) - (2*R*cos(x(2)(t) - t*w)*diff(x(1),t,2))/l^2 + (2*R*sin(x(2)(t) - t*w)*x(1)(t)*diff(x(2),t,2))/l^2 - (2*R*sin(x(2)(t) - t*w)*diff(x(1),t)*(w - diff(x(2)(t), t)))/l^2 - (2*R*sin(x(2)(t) - t*w)*(w - diff(x(2),t))*diff(x(1)(t), t))/l^2 + (2*R*x(1)(t)*cos(x(2)(t) - t*w)*(w - diff(x(2),t))*(w - diff(x(2)(t), t)))/l^2))/(8*x(1)(t)^2 + 8*R^2 - (16*R*x(1)(t)*cos(x(2)(t) - t*w))/l^2 - 8) + (2*l^2*m*(2*diff(x(1)(t), t) - (2*R*sin(x(2)(t) - t*w)*(w - diff(x(2)(t), t)))/l^2)*((2*R*diff(x(1),t)*cos(x(2)(t) - t*w))/l^2 - 2*diff(x(1),t)*x(1)(t) + (2*R*sin(x(2)(t) - t*w)*x(1)(t)*(w - diff(x(2),t)))/l^2))/(8*x(1)(t)^2 + 8*R^2 - (16*R*x(1)(t)*cos(x(2)(t) - t*w))/l^2 - 8) + (2*l^2*m*(2*x(1)(t) - (2*R*cos(x(2)(t) - t*w))/l^2)*((16*R*cos(x(2)(t) - t*w)*diff(x(1)(t), t))/l^2 - 16*x(1)(t)*diff(x(1)(t), t) + (16*R*sin(x(2)(t) - t*w)*x(1)(t)*(w - diff(x(2)(t), t)))/l^2)*((2*R*diff(x(1),t)*cos(x(2)(t) - t*w))/l^2 - 2*diff(x(1),t)*x(1)(t) + (2*R*sin(x(2)(t) - t*w)*x(1)(t)*(w - diff(x(2),t)))/l^2))/(8*x(1)(t)^2 + 8*R^2 - (16*R*x(1)(t)*cos(x(2)(t) - t*w))/l^2 - 8)^2-diff(x(2),t)^2*m*x(1) - (g*l*m*(2*x(1) - (2*R*cos(x(2) - t*w))/l^2))/(2*((2*R*x(1)*cos(x(2) - t*w))/l^2 - x(1)^2 - R^2 + 1)^(1/2)) + (l^2*m*(16*x(1) - (16*R*cos(x(2) - t*w))/l^2)*(2*diff(x(1),t)*x(1) - (2*R*diff(x(1),t)*cos(x(2) - t*w))/l^2 + (2*R*x(1)*sin(x(2) - t*w)*(diff(x(2),t) - w))/l^2)^2)/(8*R^2 + 8*x(1)^2 - (16*R*x(1)*cos(x(2) - t*w))/l^2 - 8)^2 - (2*l^2*m*(2*diff(x(1),t) + (2*R*sin(x(2) - t*w)*(diff(x(2),t) - w))/l^2)*(2*diff(x(1),t)*x(1) - (2*R*diff(x(1),t)*cos(x(2) - t*w))/l^2 + (2*R*x(1)*sin(x(2) - t*w)*(diff(x(2),t) - w))/l^2))/(8*R^2 + 8*x(1)^2 - (16*R*x(1)*cos(x(2) - t*w))/l^2 - 8);
f(2)=m*x(1)^2*diff(x(2),t,2) + 2*m*diff(x(2),t)*x(1)(t)*diff(x(1)(t), t) - (4*R*m*sin(x(2)(t) - t*w)*x(1)(t)*(2*diff(x(1),t)*diff(x(1)(t), t) + 2*x(1)(t)*diff(x(1),t,2) - (2*R*cos(x(2)(t) - t*w)*diff(x(1),t,2))/l^2 + (2*R*sin(x(2)(t) - t*w)*x(1)(t)*diff(x(2),t,2))/l^2 - (2*R*sin(x(2)(t) - t*w)*diff(x(1),t)*(w - diff(x(2)(t), t)))/l^2 - (2*R*sin(x(2)(t) - t*w)*(w - diff(x(2),t))*diff(x(1)(t), t))/l^2 + (2*R*x(1)(t)*cos(x(2)(t) - t*w)*(w - diff(x(2),t))*(w - diff(x(2)(t), t)))/l^2))/(8*x(1)(t)^2 + 8*R^2 - (16*R*x(1)(t)*cos(x(2)(t) - t*w))/l^2 - 8) + (4*R*m*sin(x(2)(t) - t*w)*diff(x(1)(t), t)*((2*R*diff(x(1),t)*cos(x(2)(t) - t*w))/l^2 - 2*diff(x(1),t)*x(1)(t) + (2*R*sin(x(2)(t) - t*w)*x(1)(t)*(w - diff(x(2),t)))/l^2))/(8*x(1)(t)^2 + 8*R^2 - (16*R*x(1)(t)*cos(x(2)(t) - t*w))/l^2 - 8) - (4*R*m*x(1)(t)*cos(x(2)(t) - t*w)*(w - diff(x(2)(t), t))*((2*R*diff(x(1),t)*cos(x(2)(t) - t*w))/l^2 - 2*diff(x(1),t)*x(1)(t) + (2*R*sin(x(2)(t) - t*w)*x(1)(t)*(w - diff(x(2),t)))/l^2))/(8*x(1)(t)^2 + 8*R^2 - (16*R*x(1)(t)*cos(x(2)(t) - t*w))/l^2 - 8) + (4*R*m*sin(x(2)(t) - t*w)*x(1)(t)*((16*R*cos(x(2)(t) - t*w)*diff(x(1)(t), t))/l^2 - 16*x(1)(t)*diff(x(1)(t), t) + (16*R*sin(x(2)(t) - t*w)*x(1)(t)*(w - diff(x(2)(t), t)))/l^2)*((2*R*diff(x(1),t)*cos(x(2)(t) - t*w))/l^2 - 2*diff(x(1),t)*x(1)(t) + (2*R*sin(x(2)(t) - t*w)*x(1)(t)*(w - diff(x(2),t)))/l^2))/(8*x(1)(t)^2 + 8*R^2 - (16*R*x(1)(t)*cos(x(2)(t) - t*w))/l^2 - 8)^2-(16*R*m*x(1)*sin(x(2) - t*w)*(2*diff(x(1),t)*x(1) - (2*R*diff(x(1),t)*cos(x(2) - t*w))/l^2 + (2*R*x(1)*sin(x(2) - t*w)*(diff(x(2),t) - w))/l^2)^2)/(8*R^2 + 8*x(1)^2 - (16*R*x(1)*cos(x(2) - t*w))/l^2 - 8)^2 - (2*l^2*m*((2*R*diff(x(1),t)*sin(x(2) - t*w))/l^2 + (2*R*x(1)*cos(x(2) - t*w)*(diff(x(2),t) - w))/l^2)*(2*diff(x(1),t)*x(1) - (2*R*diff(x(1),t)*cos(x(2) - t*w))/l^2 + (2*R*x(1)*sin(x(2) - t*w)*(diff(x(2),t) - w))/l^2))/(8*R^2 + 8*x(1)^2 - (16*R*x(1)*cos(x(2) - t*w))/l^2 - 8) - (R*g*m*x(1)*sin(x(2) - t*w))/(l*((2*R*x(1)*cos(x(2) - t*w))/l^2 - x(1)^2 - R^2 + 1)^(1/2));
end
